def process_tag(doc, para, tag):
    if tag.name == "h1":
        if "id" in tag.attrs and tag["id"] == "chapter-title":
            doc.add_paragraph(style="H1 - Chapter").add_run(tag.text)
        else:
            doc.add_paragraph(style="H1 - Section").add_run(tag.text)
    elif tag.name == "h2":
        doc.add_paragraph(style="H1 - Section").add_run(tag.text)
    elif tag.name == "h3":
        doc.add_paragraph(style="H2 - Heading").add_run(tag.text)
    elif tag.name == "h4":
        doc.add_paragraph(style="H3 - Subheading").add_run(tag.text)
    elif tag.name == "p":
        if para is None:
            para = doc.add_paragraph(style="P - Regular")
        for content in tag.contents:
            if content.name is None:
                para.add_run(str(content))
            else:
                process_tag(doc, para, content)
    elif tag.name == "div" and "data-custom-style" in tag.attrs:
        style_name = "P - Regular"
        custom_style = tag["data-custom-style"]
        if custom_style == "SP - Editorial":
            style_name = "SP - Editorial"
        elif custom_style == "P - Callout Heading":
            style_name = "P - Callout Heading"
        elif custom_style == "P - Callout":
            style_name = "P - Callout"
        elif custom_style == "IMG - Caption":
            style_name = "IMG - Caption"
        para = doc.add_paragraph(style=style_name)
        contents = tag.contents
        if len(contents) > 1 and contents[0].strip() == "":
            contents = contents[1:]
        if len(contents) > 1 and contents[-1].strip() == "":
            contents = contents[:-1]
        for content in contents:
            if content.name is None:
                para.add_run(str(content))
            else:
                process_tag(doc, para, content)
    elif tag.name == "pre":
        para = doc.add_paragraph(
            style="L - Source"
            if para is not None and para.style.name.startswith("L")
            else "SC - Source"
        ).add_run(tag.find("code").text)
    elif tag.name == "a":
        para.add_run(tag.text, style="P - URL")
    elif tag.name == "em":
        para.add_run(tag.text, style="P - Italics")
    elif tag.name == "strong":
        if len(tag.contents) == 1 and tag.contents[0].name == "em":
            para.add_run(tag.text, style="P - Keyword")
        else:
            para.add_run(tag.text, style="P - Bold")
    elif tag.name == "code":
        para.add_run(tag.text, style="P - Code")
    elif tag.name == "li":
        para = doc.add_paragraph(
            style="L - Numbers" if tag.parent.name == "ol" else "L - Bullets"
        )
        for content in tag.contents:
            if content.name is None:
                para.add_run(str(content).replace("\n", ""))
            else:
                process_tag(doc, para, content)
    elif tag.name == "table":
        rows = tag.find_all("tr")
        num_cols = len(rows[0].find_all(["th", "td"]))
        word_table = doc.add_table(rows=1, cols=num_cols)

        for idx, cell in enumerate(rows[0].find_all(["th", "td"])):
            paragraph = word_table.cell(0, idx).paragraphs[0]
            paragraph.add_run(cell.get_text(strip=True))

        for _, row in enumerate(rows[1:], start=1):
            word_row = word_table.add_row().cells
            for col_idx, cell in enumerate(row.find_all("td")):
                paragraph = word_row[col_idx].paragraphs[0]
                paragraph.add_run(cell.get_text(strip=True))

        for row in word_table.rows:
            for cell in row.cells:
                tc = cell._tc
                tcPr = tc.get_or_add_tcPr()
                for side in ("top", "start", "bottom", "end"):
                    side_border = docx.oxml.OxmlElement(f"w:{side}")
                    side_border.set(docx.oxml.ns.qn("w:val"), "single")
                    side_border.set(docx.oxml.ns.qn("w:sz"), "4")
                    side_border.set(docx.oxml.ns.qn("w:space"), "0")
                    side_border.set(docx.oxml.ns.qn("w:color"), "auto")
                    tcPr.append(side_border)
    elif tag.name == "img":
        image_url = tag["src"]
        try:
            image_path = image_url
            if image_url.startswith("http"):
                response = requests.get(image_url, stream=True)
                if response.status_code == 200:
                    temp_img = tempfile.NamedTemporaryFile(delete=False, suffix=".png")
                    for chunk in response.iter_content(1024):
                        temp_img.write(chunk)
                    temp_img.close()
                    image_path = temp_img.name
                else:
                    raise Exception(
                        f"Image could not be retrieved: {response.status_code}"
                    )
            para = doc.add_paragraph()
            para.alignment = docx.enum.text.WD_PARAGRAPH_ALIGNMENT.CENTER
            para.add_run().add_picture(image_path, width=docx.shared.Inches(4.5))
        except Exception as e:
            print(f"An error occurred while adding the image: {e}")
    elif isinstance(tag, bs4.element.NavigableString):
        if para is not None:
            para.add_run(str(tag))
    else:
        for content in tag.contents:
            process_tag(doc, para, content)


def process_html(html_content, doc):
    soup = bs4.BeautifulSoup(html_content, "html.parser")
    for content in soup.contents:
        process_tag(doc, None, content)


